// import React from 'react';
// import { render } from 'react-dom';
import React, { Component } from "react";

class Picker extends React.Component {

  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = { random: 0 };
  }

  handleClick() {
    const min = Math.ceil(1);
    const max = Math.floor(70);
    const rand = Math.floor(Math.random() * (max - min + 1)) + min;
    this.setState({ random: this.state.random + rand });
  }

  render() {
    return (
      <div className="">
      <h2>Lucky Number Generator</h2><br /><br />
      <button className="ui inverted yellow button" onClick={this.handleClick.bind(this)}>Click</button>
        <div className="card" style={{marginTop:"10px"}}>
          <div className="card-block">
            <p>The number is: {this.state.random}</p>
          </div>
        </div>
      </div>
    );
  }
}

// render(<Picker />, document.getElementById(''));
export default Picker;
