const initialState = {
  rounds: [],
  loading: true,
};

const rounds = (state = initialState, action) => {
  switch (action.type) {
    case "LOADING":
      return {
        ...state,
        loading: true,
      };
    case "SET_ROUNDS":
      return {
        ...state,
        loading: false,
        rounds: action.rounds
      };
    case "ADD_ROUND":
      return {
        ...state,
        rounds: [...state.rounds, 
        {
          user: state.userId,
          round: action.round
        },
      ],
    }
    case "FIND_USER_ROUND":
      const findRound = state.findIndex(round => round.id === action.id);
      return {
        ...state,
        rounds: [...state.rounds, action.round]
      };

    default:
      return state;
  }
};

export default rounds;



